// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

contract TodoApp2 {
    struct TodoItem {
        uint256 id;
        //date
        string title;
        //string description;
        //address walletAddress;
        //string status;
        //string priority;
    }
    //TodoItem todoItem;
    event createdTask(uint256 indexed _memberId);

    mapping(uint256 => TodoItem) todoItems;
    uint256[] public todos;

    function addTodo(uint256 id) public {
        TodoItem storage newTodo = todoItems[id];
        newTodo.title = "Title Text";
        newTodo.id = id;
        todos.push(id);
        //todoItem = TodoItem(id,"Task 1", "Description for task 1", 0x4feA2E2b93C671C37f691b612ef59c0649775c9C, "In Progress", "Important");
    }

    function checkTodos() public view returns (uint256) {
        return todos.length;
    }

    /*function getAllToDos() public view returns (TodoItem[] memory) {
        return todoItems;
    }*/

    function getTodo(uint256 id) external view returns (TodoItem memory) {
        TodoItem storage item = todoItems[id];
        return item;
    }
}
